bolt.onrender3d(function (event)
  if not (checkframe or any3dobjectexists) then return end
  local f = render3dlookup[event:vertexcount()]
  if f ~= nil then
    local model = f(event)
    if model and model.dohighlight then
      any3dobjectfound = true
      if (bolt.time() % 600000) <= 480000 then
        local m = model.center
        if model.anim then
          m = m:transform(event:vertexanimation(1))
        end
        --drawbox(m:transform(event:modelmatrix()), event:viewmatrix(), event:projectionmatrix(), model.boxsize, model.boxthickness)
      end
      if checkframe then
        model.foundoncheckframe = true
      end
    end
  end
end)

bolt.onrendericon(function (event)
  if not checkframe then return end
  local modelcount = event:modelcount()
  local f = nil
  if modelcount == 1 then
    f = rendericonlookup1[event:modelvertexcount(1)]
  elseif modelcount == 2 then
    f = rendericonlookup2[event:modelvertexcount(1)]
  end
  if f ~= nil then
    nextrender2dbuff, nextrender2ddebuff = f(event)
    nextrender2dpxleft, nextrender2dpxtop, _, _ = event:xywh()
  end
end)

startcheckframe = function (t)
  for _, buff in pairs(buffs) do
    buff.foundoncheckframe = false
  end
  for _, model in pairs(models) do
    model.foundoncheckframe = false
  end
  for _, element in pairs(elements) do
    element.foundoncheckframe = false
  end
  for _, particle in pairs(particles) do
    particle.foundoncheckframe = false
  end
end

endcheckframe = function (t)
  for _, buff in pairs(buffs) do
    if not buff.foundoncheckframe then
      buff.active = false
    end
  end
  for _, element in pairs(elements) do
    if not element.foundoncheckframe then
        element.active = false
    end
  end
end

bolt.onswapbuffers (function (event)
  t = bolt.time()
  if checkframe then
    endcheckframe(t)
    checkframe = false
  end

  if t > checktime + checkinterval then
    checkframe = true
    checktime = checktime + checkinterval
    if checktime < t then
      checktime = t
    end
    startcheckframe(t)
  end
  if (elements.bank.active or elements.central.active or elements.lobby.active) and not elements.loot.active then
    return
  else

    miscui = images['gauge-ui'].misc
    ovlimg = miscui.overloaded
    auraimg = miscui.aura
    summimg = miscui.summon
    poisonimg = miscui.poisonous
    prayerimg = miscui.prayer

    ovlx = bx
    ovly = by
    prayerx = ovlx + 30
    prayery = by
    summx = prayerx + 30
    summy = by
    poisonx = summx + 30
    poisony = by
    aurax = poisonx + 30
    auray = by

    if buffs.overload.active and buffs.overload.number < (10) then
      ovlimg.surface:drawtoscreen(0, 0, ovlimg.width, ovlimg.height, ovlx * scale, ovly * scale, ovlimg.width * scale, ovlimg.height * scale)
    end

    if stats.prayer.fraction < (0.2) then
      prayerimg.surface:drawtoscreen(0, 0, prayerimg.width, prayerimg.height, prayerx * scale, prayery * scale, prayerimg.width * scale, prayerimg.height * scale)
    end

    if buffs.summon.active and buffs.summon.number < (30) then
      summimg.surface:drawtoscreen(0, 0, summimg.width, summimg.height, summx * scale, summy * scale, summimg.width * scale, summimg.height * scale)
    end

    if buffs.poisonous.active and buffs.poisonous.number < (10) then
      poisonimg.surface:drawtoscreen(0, 0, poisonimg.width, poisonimg.height, poisonx * scale, poisony * scale, poisonimg.width * scale, poisonimg.height * scale)
    end

    if buffs.aura.active and buffs.aura.number < (60) then
      auraimg.surface:drawtoscreen(0, 0, auraimg.width, auraimg.height, aurax * scale, auray * scale, auraimg.width * scale, auraimg.height * scale)
    end



    if buffs.livingdeath.active or buffs.sanctity.active or buffs.sorrow.active or buffs.ruination.active or buffs.skeletonwarrior.active then
      cbstyle = "necromancy"
    elseif buffs.sunshine.active or buffs.gsunshine.active or buffs.augury.active or buffs.torment.active or buffs.affliction.active or buffs.metamorphosis.active then
      cbstyle = "magic"
    elseif buffs.deathsswiftness.active or buffs.gdeathsswiftness.active or buffs.rigour.active or buffs.anguish.active or buffs.desolation.active then
      cbstyle = "ranged"
    elseif buffs.berserk.active or buffs.piety.active or buffs.turmoil.active or buffs.malevolence.active then
      cbstyle = "melee"
    end
    if cbstyle == "none" then
      return
    elseif cbstyle == "necromancy" then ----------------------------------------------- necromancy
      require("gauges.necromancy_" .. intstyle)
      drawnecrogauge()

    elseif cbstyle == "magic" then ---------------------------------------------------- magic
      require("gauges.magic_" .. intstyle)
      drawmagegauge()

    elseif cbstyle == "ranged" then ---------------------------------------------------- magic
      require("gauges.ranged_" .. intstyle)
      drawrangedgauge()

    elseif cbstyle == "melee" then ---------------------------------------------------- magic
      require("gauges.melee_" .. intstyle)
      drawmeleegauge()
    end
--this is the end for the onswapbuffers event
end
end)